function extractPixel(colorArray, index) {
  return [colorArray[index * 4], colorArray[index * 4 + 1], colorArray[index * 4 + 2], colorArray[index * 4 + 3]];
}

function extractSleeveColors(colorArray, width, height) {
  function getPixelIndex(index) {
    let x = index % 16 * 8;
    let y = Math.floor(index / 16) * 32;
    return y * width + x;
  }

  function extractSleeveColor(index, yOffset = 0) {
    const pixelIdx = getPixelIndex(shirtIdx) + yOffset;
    const dyePixelIdx = pixelIdx + 128;
    let [,,, a] = extractPixel(colorArray, dyePixelIdx);

    if (a > 0) {
      return {
        color: colorArrayToHex(extractPixel(colorArray, dyePixelIdx)),
        dyeable: 1
      };
    }

    return {
      color: colorArrayToHex(extractPixel(colorArray, pixelIdx)),
      dyeable: 0
    };
  }

  const result = [];
  let shirtIdx = 0;

  while (getPixelIndex(shirtIdx) * 4 < colorArray.length) {
    let {
      color: light,
      dyeable: l
    } = extractSleeveColor(shirtIdx, 2 * width);
    let {
      color: mid,
      dyeable: m
    } = extractSleeveColor(shirtIdx, 3 * width);
    let {
      color: dark,
      dyeable: d
    } = extractSleeveColor(shirtIdx, 4 * width);
    result[shirtIdx] = {
      light,
      mid,
      dark,
      dyeable: l << 2 | m << 1 | d << 0
    };
    shirtIdx++;
  }

  return result;
}

function extractBodyColors(colorArray) {
  const extractHex = idx => colorArrayToHex(extractPixel(colorArray, idx));

  return {
    sleeve: [258, 257, 256].map(extractHex),
    skin: [262, 261, 260].map(extractHex),
    eye: [277, 276].map(extractHex)
  };
}

class SheetDataStore {
  static id = "base";
  static columns = 1;
  static deltaX = 0;
  static deltaY = 0;
  static omittable = false;
  static size = {
    width: 20,
    height: 20
  };
  _spritesheet = null;
  _clothesData = null;

  static getSpriteFromIndex(index, offsetX = 0, offsetY = 0) {
    let x = index % this.columns * this.deltaX + offsetX;
    let y = Math.floor(index / this.columns) * this.deltaY + offsetY;
    return {
      x,
      y
    };
  }

  setSpritesheet(file) {
    const {
      width,
      height,
      blobURL
    } = file;
    this._spritesheet = {
      width,
      height,
      blobURL
    };
  }

  setClothesData(file) {
    this._clothesData = file;
  }

  get getListItemKey() {
    return index => `${this.constructor.id}-${index}`;
  }

  get getDefaultColor() {
    return index => [255, 255, 255];
  }

  get getDyeable() {
    return index => false;
  }

  get getPrismatic() {
    return index => false;
  }

  get getItemNameFromIndex() {
    return index => `${this.constructor.id}.name.${index}`;
  }

  get getDescriptionFromIndex() {
    return index => `${this.constructor.id}.desc.${index}`;
  }

}

class HatsSheetStore extends SheetDataStore {
  static id = "hats";
  static columns = 12;
  static deltaX = 20;
  static deltaY = 80;
  static omittable = true;
  _clothesData = hatsData;

  constructor() {
    super();
    makeObservable(this, {
      _spritesheet: observable,
      _clothesData: observable,
      setSpritesheet: action,
      setClothesData: action,
      getUncoloredSpriteFromIndex: computed,
      getColoredSpriteFromIndex: computed,
      getPrismaticSpriteFromIndex: computed,
      getHairDrawType: computed,
      getIgnoreHairstyleOffset: computed,
      count: computed
    });
  }

  get getUncoloredSpriteFromIndex() {
    return index => {
      const data = this._clothesData[index];
      if (data === undefined) return null;
      return data.prismatic ? null : this.constructor.getSpriteFromIndex(index, 0, 0);
    };
  }

  get getColoredSpriteFromIndex() {
    return index => null;
  }

  get getPrismaticSpriteFromIndex() {
    return index => {
      const data = this._clothesData[index];
      if (data === undefined) return null;
      return data.prismatic ? this.constructor.getSpriteFromIndex(index, 0, 0) : null;
    };
  }

  get getHairDrawType() {
    return index => this._clothesData[index]?.hairDrawType ?? 0;
  }

  get getIgnoreHairstyleOffset() {
    return index => this._clothesData[index]?.ignoreHairstyleOffset ?? false;
  }

  get getMask() {
    return index => this._clothesData[index]?.isMask ?? false;
  }

  get getPrismatic() {
    return index => this._clothesData[index]?.prismatic ?? false;
  }

  get count() {
    return this._clothesData.length;
  }

}

